import React, { Component } from 'react';
import axios from "axios"
import { withStyles } from '@material-ui/core/styles';
import { makeStyles } from '@material-ui/core/styles';
import TableContainer from '@material-ui/core/TableContainer';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import {
  userContext, getUserOwnerRestaurant,
  getUserCount, getUserAll
} from '../utils/userContext';
const styles = theme => ({
  backdrop: {
    zIndex: theme.zIndex.drawer + 1,
    color: '#fff',
  },
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  content: {
    padding: 0
  },
  item: {
    padding: theme.spacing(3),
    textAlign: 'center',
    [theme.breakpoints.up('md')]: {
      '&:not(:last-of-type)': {
        borderRight: `1px solid ${theme.palette.divider}`
      }
    },
    [theme.breakpoints.down('sm')]: {
      '&:not(:last-of-type)': {
        borderBottom: `1px solid ${theme.palette.divider}`
      }
    }
  },
  titleWrapper: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  label: {
    marginLeft: theme.spacing(1)
  },
  overline: {
    marginTop: theme.spacing(1)
  },
  paperModal: {
    position: 'absolute',
    marginLeft: "33%",
    marginTop: "5%",
    width: '33%',
    height: 540,
    backgroundColor: "#fff",
    border: 'none',
  }
});
const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '75ch',
    },
  },
}));
class ServiceFee extends Component {
  constructor(props) {
    super(props)
    this.state = {
      food: '',
      experience: '',
      user: '',
      experience1: ""
    }
  }
  componentDidMount = async () => {
    let user = await userContext();
    if (user != null) {
      this.setState({
        user: JSON.parse(user),
        isLogin: true,
      })
    } else {
      // back him login
      this.setState({
        isLogin: false
      })
    }
    let countUser = await getUserCount();
    this.setState({
      countUser: countUser[0].userDataLength
    }, () => {
      // console.log("CountUser", countUser[0].userDataLength )
    })

    // Call user
    let customer = await getUserAll(20, 19);

    let customerToCsv = [];
    for (let cus in customer) {
      delete customer[cus].password_user;
      customerToCsv.push(customer[cus])
    }

    if (customer.length > 0) {
      this.setState({
        customer: customer,
        filterResultCustomers: customer,
        showProgress: false,
        customerToCsv: customerToCsv
      })
    }
    let customerOwner = await getUserOwnerRestaurant(this.state.user.token);
    if (customerOwner.length > 0) {
      this.setState({
        customerOwner: customerOwner,
        showProgress: false
      })
    }
    // console.log("Customer owner ", customerOwner)
  }
  render() {
    return (
      <>
        <TableContainer style={{
          marginTop: 80,
          paddingLeft: 20,
          paddingRight: 20,
          width: window.innerWidth - 300,
        }}>
        </TableContainer>
      </>
    )
  }
};

export default withStyles(styles)(ServiceFee)
