import React, { Component } from 'react';
import axios from "axios"
import { withStyles } from '@material-ui/core/styles';
import TableContainer from '@material-ui/core/TableContainer';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import InputBase from '@material-ui/core/InputBase';
import { lighten, makeStyles, fade, useTheme } from '@material-ui/core/styles';
import { urlFunction } from '../utils/urls';
import LinearProgress from '@material-ui/core/LinearProgress';
import { Bar } from 'react-chartjs-2';
import {
  userContext, getUserOwnerRestaurant,
  getUserCount, getUserAll
} from '../utils/userContext';

const state = {
  labels: ['1', '2', '3',
    '4', '5',],
  datasets: [
    {
      label: 'Driving Distance',
      backgroundColor: 'rgba(75,192,192,1)',
      borderColor: 'rgba(0,0,0,1)',
      borderWidth: 2,
      data: [65, 59, 80, 81, 56,]
    }
  ]
}
const Annual_Sales = {
  labels: ['1', '2', '3',
    '4', '5',],
  datasets: [
    {
      label: 'Annual Sales',
      backgroundColor: 'rgba(75,192,192,1)',
      borderColor: 'rgba(0,0,0,1)',
      borderWidth: 2,
      data: [65, 59, 80, 81, 56,]
    }
  ]
}
const Average_Time_Order= {
  labels: ['1', '2', '3',
    '4', '5',],
  datasets: [
    {
      label: 'Average Time Order',
      backgroundColor: 'rgba(75,192,192,1)',
      borderColor: 'rgba(0,0,0,1)',
      borderWidth: 2,
      data: [65, 59, 80, 81, 30,]
    }
  ]
}
const styles = theme => ({
  backdrop: {
    zIndex: theme.zIndex.drawer + 1,
    color: '#fff',
  },
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  content: {
    padding: 0
  },
  item: {
    padding: theme.spacing(3),
    textAlign: 'center',
    [theme.breakpoints.up('md')]: {
      '&:not(:last-of-type)': {
        borderRight: `1px solid ${theme.palette.divider}`
      }
    },
    [theme.breakpoints.down('sm')]: {
      '&:not(:last-of-type)': {
        borderBottom: `1px solid ${theme.palette.divider}`
      }
    }
  },
  titleWrapper: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  label: {
    marginLeft: theme.spacing(1)
  },
  overline: {
    marginTop: theme.spacing(1)
  },
  paperModal: {
    position: 'absolute',
    marginLeft: "33%",
    marginTop: "5%",
    width: '33%',
    height: 540,
    backgroundColor: "#fff",
    border: 'none',
  },
  search1: {
    width: 300,
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: '#FFF',
    elevation: 6,
    '&:hover': {
      backgroundColor: fade('#FFF', 0.8),
    },
    marginRight: theme.spacing(2),
    marginBottom: theme.spacing(2),
    marginTop: 16,
    [theme.breakpoints.up('sm')]: {
    },
  },
  TableRowDesign: {
    "&:hover": {
      backgroundColor: "#eee", cursor: "pointer"
    },
  },
});
const useStyles = makeStyles((theme) => ({
  // root: {
  //   '& > *': {
  //     margin: theme.spacing(1),
  //     width: '75ch',
  //   },
  // },
  formControl: {
    margin: theme.spacing(5),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(5),
  },
}));
class Graphs extends Component {
  constructor(props) {
    super(props)
    this.state = {
      graphUpdate: 0,
      age: ''
    }
  }
  updateGraph = (value) => {
    this.setState({ graphUpdate: value })
  }
  handleChange = (event) => {
    this.setState({ age: event.target.value })
  }
  render() {
    const { classes } = this.props;
    console.log('my user is', this.state.user)
    return (
      <>
        <TableContainer style={{
          marginTop: 80,
          paddingLeft: 20,
          paddingRight: 20,
          width: window.innerWidth - 300,
        }}>
          {this.state.showProgress == true ?
            <LinearProgress />
            : null
          }
          <div>
            <ul>
              <li style={{ fontSize: '20px', fontWeight: 'bold', cursor: 'pointer', }} onClick={() => this.updateGraph(1)}>Distance Driving</li>
              <li style={{ fontSize: '20px', fontWeight: 'bold', cursor: 'pointer', }} onClick={() => this.updateGraph(2)}>Hourly/Daily/ Weekly/Monthly/Quarterly/ Annual Sales</li>
              <li style={{ fontSize: '20px', fontWeight: 'bold', cursor: 'pointer', }} onClick={() => this.updateGraph(3)}>Average Time of order completion</li>
              <li style={{ fontSize: '20px', fontWeight: 'bold', cursor: 'pointer', }} onClick={() => this.updateGraph(4)}>Average expenses per order</li>
              <li style={{ fontSize: '20px', fontWeight: 'bold', cursor: 'pointer', }} onClick={() => this.updateGraph(5)}>Order Cancellation</li>

            </ul>
          </div>
          {
            this.state.graphUpdate == 1 ?
              // Distance Driving
              <>
                <FormControl className={classes.formControl}>
                  <InputLabel id="demo-customized-select-label">Resturants</InputLabel>
                  <Select className={classes.formControl}

                    labelId="demo-customized-select-label"
                    id="demo-customized-select"
                    value={this.state.age}
                    onChange={this.handleChange}
                    style={{ width: "300px" }}
                  >
                    <MenuItem value="All Resturants">
                    </MenuItem>
                    <MenuItem value={10}>Ten</MenuItem>
                    <MenuItem value={20}>Twenty</MenuItem>
                    <MenuItem value={30}>Thirty</MenuItem>
                  </Select>
                </FormControl>
                <Bar
                  data={state}
                  options={{
                    title: {
                      display: true,
                      text: 'Average Distance Driving',
                      fontSize: 20
                    },
                    legend: {
                      display: true,
                      position: 'right'
                    }
                  }}
                />
              </> :
              this.state.graphUpdate == 2 ?
                // Hourly/Daily/ Weekly/Monthly/Quarterly/ Annual Sales
                <>
                  <FormControl className={classes.formControl}>
                    <InputLabel id="demo-customized-select-label">Resturants</InputLabel>
                    <Select className={classes.formControl}

                      labelId="demo-customized-select-label"
                      id="demo-customized-select"
                      value={this.state.age}
                      onChange={this.handleChange}
                      style={{ width: "300px" }}
                    >
                      <MenuItem value="All Resturants">
                      </MenuItem>
                      <MenuItem value={10}>Ten</MenuItem>
                      <MenuItem value={20}>Twenty</MenuItem>
                      <MenuItem value={30}>Thirty</MenuItem>
                    </Select>
                  </FormControl>
                  <div style={{ display: 'flex' }}>
                    <FormControl className={classes.formControl}>
                      <InputLabel id="demo-customized-select-label">Monthly</InputLabel>
                      <Select className={classes.formControl}

                        labelId="demo-customized-select-label"
                        id="demo-customized-select"
                        value={this.state.age}
                        onChange={this.handleChange}
                        style={{ width: "300px" }}
                      >
                        <MenuItem value="All Resturants">
                        </MenuItem>
                        <MenuItem value={10}>Ten</MenuItem>
                        <MenuItem value={20}>Twenty</MenuItem>
                        <MenuItem value={30}>Thirty</MenuItem>
                      </Select>
                    </FormControl>
                    <div style={{ marginLeft: "20px" }}>
                      <FormControl className={classes.formControl}>
                        <InputLabel id="demo-customized-select-label">Weekly</InputLabel>
                        <Select className={classes.formControl}

                          labelId="demo-customized-select-label"
                          id="demo-customized-select"
                          value={this.state.age}
                          onChange={this.handleChange}
                          style={{ width: "300px" }}
                        >
                          <MenuItem value="All Resturants">
                          </MenuItem>
                          <MenuItem value={10}>Ten</MenuItem>
                          <MenuItem value={20}>Twenty</MenuItem>
                          <MenuItem value={30}>Thirty</MenuItem>
                        </Select>
                      </FormControl>
                    </div>
                    <div style={{ marginLeft: '20px' }}>
                      <div style={{ display: 'flex' }} >
                        <h3>TOTAL SALES =</h3>
                        <h4>10</h4>
                      </div>
                      <div style={{ display: 'flex' }} >
                        <h3>TOTAL REVENUE  =</h3>
                        <h4>$1000</h4>
                      </div>
                    </div>
                  </div>
                  <Bar
                    data={Annual_Sales}
                    options={{
                      title: {
                        display: true,
                        text: 'Annual Sales',
                        fontSize: 20
                      },
                      legend: {
                        display: true,
                        position: 'right'
                      }
                    }}
                  />
                </> :
                this.state.graphUpdate == 3 ?
                // /3- Average Time of order completion (micro & macro)
                  <>
                     <FormControl className={classes.formControl}>
                    <InputLabel id="demo-customized-select-label">All Resturants</InputLabel>
                    <Select className={classes.formControl}

                      labelId="demo-customized-select-label"
                      id="demo-customized-select"
                      value={this.state.age}
                      onChange={this.handleChange}
                      style={{ width: "300px" }}
                    >
                      <MenuItem value="All Resturants">
                      </MenuItem>
                      <MenuItem value={10}>Ten</MenuItem>
                      <MenuItem value={20}>Twenty</MenuItem>
                      <MenuItem value={30}>Thirty</MenuItem>
                    </Select>
                  </FormControl>
                  <div style={{ display: 'flex' }}>
                    <FormControl className={classes.formControl}>
                      <InputLabel id="demo-customized-select-label">Hour</InputLabel>
                      <Select className={classes.formControl}

                        labelId="demo-customized-select-label"
                        id="demo-customized-select"
                        value={this.state.age}
                        onChange={this.handleChange}
                        style={{ width: "300px" }}
                      >
                        <MenuItem value="All Resturants">
                        </MenuItem>
                        <MenuItem value={10}>Ten</MenuItem>
                        <MenuItem value={20}>Twenty</MenuItem>
                        <MenuItem value={30}>Thirty</MenuItem>
                      </Select>
                    </FormControl>
                    <div style={{ marginLeft: "20px" }}>
                      <FormControl className={classes.formControl}>
                        <InputLabel id="demo-customized-select-label">12</InputLabel>
                        <Select className={classes.formControl}

                          labelId="demo-customized-select-label"
                          id="demo-customized-select"
                          value={this.state.age}
                          onChange={this.handleChange}
                          style={{ width: "300px" }}
                        >
                          <MenuItem value="All Resturants">
                          </MenuItem>
                          <MenuItem value={10}>Ten</MenuItem>
                          <MenuItem value={20}>Twenty</MenuItem>
                          <MenuItem value={30}>Thirty</MenuItem>
                        </Select>
                      </FormControl>
                    </div>
                    <div style={{ marginLeft: '20px' }}>
                      <div style={{ display: 'flex' }} >
                        <h3>Average Time/order =</h3>
                        <h4>10 min</h4>
                      </div>

                    </div>
                  </div>
                  <Bar
                    data={Average_Time_Order}
                    options={{
                      title: {
                        display: true,
                        text: 'Average Time Order',
                        fontSize: 20
                      },
                      legend: {
                        display: true,
                        position: 'right'
                      }
                    }}
                  />
                  </> :
                  this.state.graphUpdate == 4 ?
                  // 4- Average expenses per order (micro & macro) 
                    <>
                       <FormControl className={classes.formControl}>
                    <InputLabel id="demo-customized-select-label">All Resturants</InputLabel>
                    <Select className={classes.formControl}

                      labelId="demo-customized-select-label"
                      id="demo-customized-select"
                      value={this.state.age}
                      onChange={this.handleChange}
                      style={{ width: "300px" }}
                    >
                      <MenuItem value="All Resturants">
                      </MenuItem>
                      <MenuItem value={10}>Ten</MenuItem>
                      <MenuItem value={20}>Twenty</MenuItem>
                      <MenuItem value={30}>Thirty</MenuItem>
                    </Select>
                  </FormControl>
                  <div style={{ display: 'flex' }}>
                    <FormControl className={classes.formControl}>
                      <InputLabel id="demo-customized-select-label">Hour</InputLabel>
                      <Select className={classes.formControl}

                        labelId="demo-customized-select-label"
                        id="demo-customized-select"
                        value={this.state.age}
                        onChange={this.handleChange}
                        style={{ width: "300px" }}
                      >
                        <MenuItem value="All Resturants">
                        </MenuItem>
                        <MenuItem value={10}>Ten</MenuItem>
                        <MenuItem value={20}>Twenty</MenuItem>
                        <MenuItem value={30}>Thirty</MenuItem>
                      </Select>
                    </FormControl>
                    <div style={{ marginLeft: "20px" }}>
                      <FormControl className={classes.formControl}>
                        <InputLabel id="demo-customized-select-label">12</InputLabel>
                        <Select className={classes.formControl}

                          labelId="demo-customized-select-label"
                          id="demo-customized-select"
                          value={this.state.age}
                          onChange={this.handleChange}
                          style={{ width: "300px" }}
                        >
                          <MenuItem value="All Resturants">
                          </MenuItem>
                          <MenuItem value={10}>Ten</MenuItem>
                          <MenuItem value={20}>Twenty</MenuItem>
                          <MenuItem value={30}>Thirty</MenuItem>
                        </Select>
                      </FormControl>
                    </div>
                    <div style={{ marginLeft: '20px' }}>
                      <div style={{ display: 'flex' }} >
                        <h3>Average Expense/All Resturants =</h3>
                        <h4>$22</h4>
                      </div>

                    </div>
                  </div>
                  <Bar
                    data={Average_Time_Order}
                    options={{
                      title: {
                        display: true,
                        text: 'Average Time Order',
                        fontSize: 20
                      },
                      legend: {
                        display: true,
                        position: 'right'
                      }
                    }}
                  />
                    </> :
                    this.state.graphUpdate == 5 ?
                      <>
                      </> :
                      <>
                      </>

          }
        </TableContainer>
      </>
    )
  }
};

export default withStyles(styles)(Graphs)
