import React, { Component } from 'react';
import { withStyles } from '@material-ui/core/styles';
import TableContainer from '@material-ui/core/TableContainer';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import './styles.css'
const styles = theme => ({
  backdrop: {
    zIndex: theme.zIndex.drawer + 1,
    color: '#fff',
  },
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  content: {
    padding: 0
  },
  item: {
    padding: theme.spacing(3),
    textAlign: 'center',
    [theme.breakpoints.up('md')]: {
      '&:not(:last-of-type)': {
        borderRight: `1px solid ${theme.palette.divider}`
      }
    },
    [theme.breakpoints.down('sm')]: {
      '&:not(:last-of-type)': {
        borderBottom: `1px solid ${theme.palette.divider}`
      }
    }
  },
  titleWrapper: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  label: {
    marginLeft: theme.spacing(1)
  },
  overline: {
    marginTop: theme.spacing(1)
  },
  paperModal: {
    position: 'absolute',
    marginLeft: "33%",
    marginTop: "5%",
    width: '33%',
    height: 540,
    backgroundColor: "#fff",
    border: 'none',
  }
});

class Review extends Component {
  constructor(props) {
    super(props)
    this.state = {
      food: ''
    }
  }
  handleChange = (event) => {
    this.setState({ food: event.target.value });
  };
  render() {
    return (
      <>
        <TableContainer style={{
          marginTop: 80,
          paddingLeft: 20,
          paddingRight: 20,
          width: window.innerWidth - 300,
        }}>
          <div className='MainContainer'>
            <p
              style={{
                margin: 10, width: '70%',
                textAlign: "left", fontSize: '30px', fontWeight: '500'
              }}>Customer Feedback/ Pale nou de eksperyans ou</p>
            <div style={{ width: '85%', margin: 10, }}>
              <p
                style={{
                  textAlign: "left", fontSize: '13px',
                }}>We would love to hear your feedback on how we can improve your experience!/ Nou ta renmen tande de eksperyans ou pou nou ka amelyore sevis nou</p>
            </div>
          </div>
          <div className='MainContainer'>
            <div style={{ width: '85%', margin: 10, }}>
              <p
                style={{
                  textAlign: "left", fontSize: '16px', fontWeight: '500'
                }}>Was your food ready on time/ Manje a te prepare le'w rive?</p>
            </div>
            <div style={{ margin: 10, }}>
            <FormControl component="fieldset">
              <RadioGroup aria-label="gender" name="gender1" value={this.state.food} onChange={this.handleChange}>
                <FormControlLabel value="Yes/Wi" control={<Radio />} label="Yes/Wi" />
                <FormControlLabel value="No/non" control={<Radio />} label="No/non" />
              </RadioGroup>
            </FormControl>
            </div>
          </div>
        </TableContainer>
      </>
    )
  }
};

export default withStyles(styles)(Review)
