import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/styles';
import { withStyles } from '@material-ui/core/styles';
import { userContext, getUserById } from '../utils/userContext';
import TableContainer from '@material-ui/core/TableContainer';
import IconButton from '@material-ui/core/IconButton';
import { Link, Redirect } from 'react-router-dom';
import Add from '@material-ui/icons/Add';
import Modal from '@material-ui/core/Modal';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import icon_camera from '../assets/images/icon_camera.png';
import tkp from '../assets/images/tkp.png';
import { Alert, AlertTitle } from '@material-ui/lab';
import axios from 'axios';
import { urlFunction } from '../utils/urls';
import { menuPath } from '../utils/img_link';
import { Table, Paper, Avatar, Button } from '@material-ui/core';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import RemoveIcon from '@material-ui/icons/Remove';
import Fab from '@material-ui/core/Fab';
import Collapse from '@material-ui/core/Collapse';
import CloseIcon from '@material-ui/icons/Close';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import Backdrop from '@material-ui/core/Backdrop';
import CircularProgress from '@material-ui/core/CircularProgress';

const styles = theme => ({
  backdrop: {
		zIndex: theme.zIndex.drawer + 1,
		color: '#fff',
	},
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  content: {
    padding: 0
  },
  item: {
    padding: theme.spacing(3),
    textAlign: 'center',
    [theme.breakpoints.up('md')]: {
      '&:not(:last-of-type)': {
        borderRight: `1px solid ${theme.palette.divider}`
      }
    },
    [theme.breakpoints.down('sm')]: {
      '&:not(:last-of-type)': {
        borderBottom: `1px solid ${theme.palette.divider}`
      }
    }
  },
  titleWrapper: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  label: {
    marginLeft: theme.spacing(1)
  },
  overline: {
    marginTop: theme.spacing(1)
  },
  paperModal: {
    position: 'absolute',
    marginLeft:"33%",
    marginTop:"5%",
    width: '33%',
    height: 540,
    backgroundColor: "#fff",
    border: 'none',
  }
});

class Review extends Component {
  constructor(props) {
    super(props)
    this.state = {
      user: {},
      userResult: {},
      photos: [],
      open: false,
      imagePreviewUrl: '',
      value: '',
      copied: false,
      openAlert: false,
      showSucess: false,
      openBackdrop: false,
    }
  }
  render() {
    return (
      <>
        <TableContainer style={{
              marginTop: 80,
              paddingLeft: 20,
              paddingRight: 20,
              width: window.innerWidth - 300,
        }}>
            <Backdrop className={classes.backdrop} open={openBackdrop} >
           <CircularProgress color="inherit" />
          </Backdrop>
        </TableContainer>
      </>
    )
  }
};

export default withStyles(styles)(Review)
